function AddMovie({ movieList, setMovieList }){
const [name, setName] = useState("");
const [poster, setPoster] = useState("");
const [rating, setRating] = useState("");
const [summary, setSummary] = useState("");
const [trailer, setTrailer] = useState("");
const history = useHistory();

  return (
  <div className = "add-movie-form">
    <TextField
      onChange = {(event) => setName(event.target.value)} 
      id="outlined-basic" 
      label="Name"
      variant="outlined"
      />
       {/* <input
        type = "text" 
        onChange = {(event) => setName(event.target.value)}
        placeholder = "Name"
         /> */}
      <TextField
        label = "Poster" 
        // placeholder = "Poster"
        onChange = {(event) => setPoster(event.target.value)}
        variant="outlined"
        />
       <TextField 
       label = "Rating" 
        //placeholder = "Rating" 
        onChange = {(event) => setRating(event.target.value)}
        variant="outlined"
        />
       <TextField 
       label = "Summary"
        // placeholder = "Summary"
         onChange = {(event) => setSummary(event.target.value)}
         variant="outlined"
         />
         <TextField 
        type="text"
        label = "Trailer"
          onChange = {(event) => setTrailer(event.target.value)}
         variant="outlined"
         />
     {/* <button onClick = {() => console.log(name, poster, rating, summary)}>Add Movie</button> */}
  {/* Copy the movieList and add new movie to it */}
  <Button
    onClick = {() => {
      const newMovie = {
        name: name,
        poster: poster,
        rating: rating,
        summary: summary,
        trailer: trailer
      };

      setMovieList([...movieList, newMovie]);
      history.push("/movies");
    } }
   variant="contained"
   >
    Add Movie</Button>
   </div>
   );
}


function EditMovie({ movieList, setMovieList }){
  const { id } = useParams();
  const movie = movieList[id];
  console.log(movie);
  
  const [name, setName] = useState(movie.name);
  const [poster, setPoster] = useState(movie.poster);
  const [rating, setRating] = useState(movie.rating);
  const [summary, setSummary] = useState(movie.summary);
  const [trailer, setTrailer] = useState(movie.trailer);
  const history = useHistory();
  
    return (
    <div className = "add-movie-form">
      <TextField
        value = {name}
        onChange = {(event) => setName(event.target.value)} 
        id="outlined-basic" 
        label="Name"
        variant="outlined"
        />
         {/* <input
          type = "text" 
          onChange = {(event) => setName(event.target.value)}
          placeholder = "Name"
           /> */}
        <TextField
          value = {poster}
          label = "Poster" 
          // placeholder = "Poster"
          onChange = {(event) => setPoster(event.target.value)}
          variant="outlined"
          />
         <TextField 
         value = {rating}
         label = "Rating" 
          //placeholder = "Rating" 
          onChange = {(event) => setRating(event.target.value)}
          variant="outlined"
          />
         <TextField 
         value = {summary}
         label = "Summary"
          // placeholder = "Summary"
           onChange = {(event) => setSummary(event.target.value)}
           variant="outlined"
           />
           <TextField 
           value = {trailer}
          type="text"
          label = "Trailer"
            onChange = {(event) => setTrailer(event.target.value)}
           variant="outlined"
           />
       {/* <button onClick = {() => console.log(name, poster, rating, summary)}>Add Movie</button> */}
    {/* Copy the movieList and add new movie to it */}
    <Button
      onClick = {() => {
        const updatedMovie = {
          name: name,
          poster: poster,
          rating: rating,
          summary: summary,
          trailer: trailer
        };
        const copyMovieList = [...movieList];
        copyMovieList[id] = updatedMovie;
        setMovieList(copyMovieList);
        history.push("/movies");
      } }
     variant="contained"
     color = "success"
     >
      Save
      </Button>
     </div>
     );
  }
  



function MovieDetails({ movieList }){
  const { id } = useParams(); // extracting parameter from the URL
  console.log(id, movieList); 

    const movie = movieList[id];
    const history = useHistory();
  return(
    <div>
      {/* right click the youtube vedio and give copy embed code  */}
      <iframe 
      width="100%" 
      height="650"
      src={movie.trailer}
      title="YouTube video player"
      frameborder="0"
      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
      allowfullscreen
      ></iframe>
    <div className="movie-detail-container">
      <div className="movie-specs">
        <h3 className="movie-name">{movie.name}</h3>
          <p className="movie-rating">⭐{movie.rating} </p>
      </div>
    <p className="movie-summary">{movie.summary}</p>
    <Button 
      variant="contained"
      onClick = {() => history.goBack()}
      // onClick = {() => history.push("/movies")}
      // onClick = {() => history.goForward()}
      startIcon={<ArrowBackIosIcon />}
      >
       Back
    </Button>
    </div>
   </div>
  ); 
  }

function MovieList({ movieList, setMovieList }){
  const history = useHistory();
  return(
<div className="movie-list">
  {movieList.map(({ name, poster, rating, summary }, index) => 
  (
    <Movie 
    key={index}
    name = {name} 
    poster={poster} 
    rating = {rating} 
    summary={summary}
    
    deleteButton = {
    <IconButton
      onClick={() => {
        console.log(index);
        const copyMovieList = [...movieList];
        copyMovieList.splice(index, 1)
        setMovieList(copyMovieList)
      }}  
      aria-label = "delete"
      color="error"
      >
       <DeleteIcon />
       </IconButton>
      }

      editButton = {
        <IconButton
          onClick={() =>history.push(`/movies/edit/${index}`)}  
          aria-label = "delete"
          color="secondary"
          >
           <EditIcon />
           </IconButton>
          }
          id={index}
    />
  ))}
</div>
);
}


